import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert, StyleSheet } from 'react-native';
import axios from 'axios';
import DateTimePicker from 'react-native-modal-datetime-picker'; // Import the DateTimePicker component

export default function BookingScreen({ route }) {
  const { hotel } = route.params;
  const [formData, setFormData] = useState({
    hotel_id: hotel.id,
    check_in_date: '',
    check_out_date: '',
    num_guests: '',
    phone_number: '',
    address: '',
    email: '',
    num_men: '0',
    num_women: '0',
    num_children: '0',
  });

  // ... (other state variables)

  const [isCheckInDatePickerVisible, setCheckInDatePickerVisibility] = useState(false);
  const [isCheckOutDatePickerVisible, setCheckOutDatePickerVisibility] = useState(false);

  const showCheckInDatePicker = () => {
    setCheckInDatePickerVisibility(true);
  };

  const handleCheckInDatePicked = (date) => {
    setFormData({ ...formData, check_in_date: date.toISOString().split('T')[0] });
    hideCheckInDatePicker();
  };

  const hideCheckInDatePicker = () => {
    setCheckInDatePickerVisibility(false);
  };

  const showCheckOutDatePicker = () => {
    setCheckOutDatePickerVisibility(true);
  };

  const handleCheckOutDatePicked = (date) => {
    setFormData({ ...formData, check_out_date: date.toISOString().split('T')[0] });
    hideCheckOutDatePicker();
  };

  const hideCheckOutDatePicker = () => {
    setCheckOutDatePickerVisibility(false);
  };

  const handleBooking = async () => {
    try {
      const serverUrl = 'https://akinbrand.com.ng/mywi-fi/'; // Update with your server endpoint
      const response = await axios.post(`${serverUrl}booking.php`, formData);

      if (response.data.message === 'Booking Successful') {
        Alert.alert('Booking Successful', 'Your booking has been confirmed.');
      } else {
        Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      }
    } catch (error) {
      Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Make a Booking</Text>
      <Text style={styles.hotelName}>{hotel.name}</Text>
      <View style={styles.input}>
        <Text>Check-In Date: {formData.check_in_date}</Text>
        <Button title="Select Check-In Date" onPress={showCheckInDatePicker} />
        <DateTimePicker
          isVisible={isCheckInDatePickerVisible}
          mode="date"
          onConfirm={handleCheckInDatePicked}
          onCancel={hideCheckInDatePicker}
        />
      </View>
      <View style={styles.input}>
        <Text>Check-Out Date: {formData.check_out_date}</Text>
        <Button title="Select Check-Out Date" onPress={showCheckOutDatePicker} />
        <DateTimePicker
          isVisible={isCheckOutDatePickerVisible}
          mode="date"
          onConfirm={handleCheckOutDatePicked}
          onCancel={hideCheckOutDatePicker}
        />
      </View>
      {/* ... (other input components) */}
      <Button title="Book Now" onPress={handleBooking} />
    </View>
  );
}

     
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  hotelName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  iconInput: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  icon: {
    marginRight: 10,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    margin: 10,
    padding: 10,
  },
  inputField: {
    flex: 1,
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    padding: 10,
  },
  datePicker: {
    width: '80%',
    margin: 10,
  },
});
