import React from 'react';
import { View, Text, Button, TextInput, StyleSheet, ScrollView, Image } from 'react-native';

export default function LandingScreen({ navigation }) {
  // Example hotel data
  const hotels = [
    {
      name: 'Luxury Hotel',
      price: 150,
      starRating: 5,
      views: 20000,
      rooms: 100,
      features: ['Pool', 'Spa', 'Restaurant'],
    },
    {
      name: 'Beach Resort',
      price: 120,
      starRating: 4,
      views: 25000,
      rooms: 80,
      features: ['Beach Access', 'Fitness Center'],
    },
    {
      name: 'City Retreat',
      price: 100,
      starRating: 4,
      views: 18000,
      rooms: 60,
      features: ['City View', 'Gym'],
    },
  ];

  const handleGetStarted = () => {
    // You can navigate to the main app screen here
    navigation.navigate('MainApp');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Discover the Best Hotels</Text>
      <TextInput
        style={styles.searchBar}
        placeholder="Search for hotels..."
      />
      <ScrollView style={styles.cardContainer}>
        {hotels.map((hotel, index) => (
          <View style={styles.card} key={index}>
            <Image source={require('./hotel-image.jpg')} style={styles.cardImage} />
            <Text style={styles.hotelName}>{hotel.name}</Text>
            <Text style={styles.price}>${hotel.price} per night</Text>
            <Text style={styles.rating}>Rating: {hotel.starRating} stars</Text>
            <Text style={styles.views}>{hotel.views} views</Text>
            <Text style={styles.rooms}>Rooms: {hotel.rooms}</Text>
            <Text style={styles.features}>
              Features: {hotel.features.join(', ')}
            </Text>
          </View>
        ))}
      </ScrollView>
      <Button title="Get Started" onPress={handleGetStarted} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    padding: 20,
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  searchBar: {
    width: '100%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    padding: 10,
    marginBottom: 20,
  },
  cardContainer: {
    width: '100%',
  },
  card: {
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 10,
    margin: 10,
  },
  cardImage: {
    width: '100%',
    height: 150,
    resizeMode: 'cover',
    borderRadius: 10,
  },
  hotelName: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  price: {
    fontSize: 16,
  },
  rating: {
    fontSize: 16,
  },
  views: {
    fontSize: 16,
  },
  rooms: {
    fontSize: 16,
  },
  features: {
    fontSize: 16,
  },
});
