import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert, StyleSheet } from 'react-native';
import axios from 'axios';

export default function LoginScreen({ navigation }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // New state to track login status

  const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';

  const loginUser = async () => {
    try {
      const response = await axios.post(`${serverUrl}login.php`, {
        username,
        password,
      });

      if (response.data.message === 'Login Successful') {
        // Set the login status to true
        setLoggedIn(true);
        // Clear the input fields
        setUsername('');
        setPassword('');
      } else {
        // Display an error message for unsuccessful login
        Alert.alert('Login Error', 'Invalid username or password. Please try again.');
      }
    } catch (error) {
      // Display an error message for unsuccessful login
      Alert.alert('Login Error', 'Invalid username or password. Please try again.');
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      {loggedIn ? ( // Conditionally render content based on login status
        <View>
          <Text style={styles.header}>Welcome to the Dashboard</Text>
          {/* Add your dashboard content here */}
        </View>
      ) : (
        <View>
          <Text style={styles.header}>Login</Text>
          <TextInput
            style={styles.input}
            placeholder="Username"
            onChangeText={(text) => setUsername(text)}
            id="username"
          />
          <TextInput
            style={styles.input}
            placeholder="Password"
            secureTextEntry={true}
            onChangeText={(text) => setPassword(text)}
            id="password"
          />
          <Button title="Login" onPress={loginUser} />
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    margin: 10,
    padding: 10,
  },
});
