import React from 'react';
import { View, Text, Alert, StyleSheet } from 'react-native';
import axios from 'axios';

export default function WelcomeScreen({ route }) {
  const { username } = route.params;

  const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';

  const getUserDetails = async () => {
    try {
      const response = await axios.post(`${serverUrl}getUserDetails.php`, {
        username,
      });

      // Display a success message
      Alert.alert('getUserDetails Successful', response.data.message);
    } catch (error) {
      // Display an error message
      Alert.alert('getUserDetails Error', 'An error occurred while registering. Please try again.');
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Dashboard</Text>
      <Text>Welcome, {username}!</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
});
