import React from 'react';
import { View, Text, Alert, StyleSheet } from 'react-native';

export default function WelcomeScreen({ route }) {
  const { username } = route.params;

  return (
    <View style={styles.container}>
      <View style={styles.headerBanner}>
        <Text style={styles.header}>Welcome</Text>
      </View>
      <Text style={styles.usernameText}>Welcome, {username}!</Text>
      {/* Add more content as needed */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  headerBanner: {
    backgroundColor: 'blue', // Set the background color of the banner
    width: '100%',
    padding: 10,
    alignItems: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white', // Set the text color of the header
  },
  usernameText: {
    fontSize: 16,
    marginTop: 20,
  },
});
