import React, { useState } from 'react';
import { View, Text, TextInput, StyleSheet, ScrollView, Image, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();

export default function WelcomeScreen({ navigation }) {
{/*Hotel details start here */}
  const hotels = [
    {
      id: 1,
      name: 'Luxury Hotel',
      price: 150,
      starRating: 5,
      views: 20000,
      Bedrooms: 4,
      features: ['Hotel', 'pool', 'Restaurant'],
      images: [require('./hotel.jpg'), require('./pool.jpg'), require('./restaurant.jpg')],
    },
    {
      id: 2,
      name: 'Beach Resort',
      price: 120,
      starRating: 4,
      views: 25000,
      Bedrooms: 3,
      features: ['Hotel', 'pool', 'Restaurant'],
      images: [require('./hotel1.jpg'), require('./pool1.jpg'), require('./restaurant1.jpeg')],
    },
    {
      id: 3,
      name: 'City Retreat',
      price: 100,
      starRating: 4,
      views: 18000,
      Bedrooms: 5,
      features: ['Hotel', 'pool', 'Restaurant'],
      images: [require('./hotel3.jpg'), require('./pool2.jpg'), require('./restaurant2.jpg')],
    },
    {
      id: 4,
      name: 'City Retreat',
      price: 100,
      starRating: 4,
      views: 18000,
      Bedrooms: 3,
      features: ['Hotel', 'pool', 'Restaurant'],
      images: [require('./hotel4.jpg'), require('./pool3.jpg'),  require('./restaurant3.png')],
    },
  ];

{/*Hotel details stop here */}



{/*Feature details start here */}
  const [selectedFeatureImages, setSelectedFeatureImages] = useState(
    hotels.map(() => hotels[0].images[0])
  );

  const handleFeatureClick = (featureImage, index) => {
    const updatedImages = [...selectedFeatureImages];
    updatedImages[index] = featureImage;
    setSelectedFeatureImages(updatedImages);
  };

  {/*Feature details stop here */}


  {/*Sear function start here */}
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredHotels, setFilteredHotels] = useState(hotels);

  const handleSearch = () => {
    const filtered = hotels.filter((hotel) => {
      const name = hotel.name.toLowerCase();
      const query = searchQuery.toLowerCase();
      return name.includes(query);
    });
    setFilteredHotels(filtered);
  };
{/*Search function stop here */}



{/*navigate to login and register start here */}
  const handleLogin = () => {
    navigation.navigate('Login');
  };

  const handleRegistration = () => {
    navigation.navigate('Registration');
  };
{/*navigate to login and register stop here */}


  return (
    <View style={styles.container}>
      <Text style={styles.header}>Discover the Best Hotels</Text>
      
      {/* Search bar and button start here */}
      <TextInput
        style={styles.searchBar}
        placeholder="Search for hotels..."
        value={searchQuery}
        onChangeText={(text) => setSearchQuery(text)}
      />
      <TouchableOpacity style={styles.searchButton} onPress={handleSearch}>
        <Text style={styles.searchButtonText}>Search</Text>
      </TouchableOpacity>

      {/* Search bar and button stop here */}

       {/*hotel details mapping start here */}
       <ScrollView contentContainerStyle={styles.cardContainer}>

       {filteredHotels.map((hotel, index) => (
          <TouchableOpacity
            style={styles.card}
            key={hotel.id}
            onPress={() => navigation.navigate('HotelDetails', { hotel })}
          >


      
            <Image source={selectedFeatureImages[index]} style={styles.cardImage} />
            <Text style={styles.hotelName}>{hotel.name}</Text>
            <Text style={styles.price}>#{hotel.price} per night</Text>
            <Text style={styles.rating}>
              <Icon name="star" size={20} color="gold" /> {hotel.starRating} stars
            </Text>
            <Text style={styles.views}>{hotel.views} views</Text>
            <Text style={styles.Bedrooms}>Bedrooms: {hotel.Bedrooms}</Text>

 {/*hotel details mapping stop here */}


 {/*eatures details mapping start here */}
            <View style={styles.featuresContainer}>

              {hotel.features.map((feature, i) => (
                <TouchableOpacity
                  key={i}
                  style={styles.feature}
                  onPress={() => handleFeatureClick(hotel.images[i], index)}
                >
                  <Icon name="check" size={16} color="green" />
                  <Text style={styles.featureText}>{feature}</Text>
                </TouchableOpacity>
              ))}
            </View>
{/*features details mapping stop here */}


            <TouchableOpacity
              style={styles.bookNowButton}
              onPress={() => {
                // Handle booking functionality here
                // You can add code to navigate to the booking screen with hotel information
                navigation.navigate('Booking', { hotel });
              }}
            >
              <Text style={styles.bookNowText}>Book Now</Text>
            </TouchableOpacity>
          </TouchableOpacity>
        ))}
      </ScrollView>

{/*Navigate to login and register start here */}
      <TouchableOpacity style={styles.getStartedButton} onPress={handleRegistration}>
        <Text style={styles.getStartedButtonText}>Registration</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.loginButton} onPress={handleLogin}>
        <Text style={styles.loginButtonText}>Login</Text>
      </TouchableOpacity>
    </View>
  );
}
{/*Navigate to login and register stop here */}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    padding: 20,
    backgroundColor: 'lightgray',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  searchBar: {
    width: '100%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    padding: 10,
    marginBottom: 10,
  },
  searchButton: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    width: 100,
    alignSelf: 'center',
  },
  searchButtonText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
  },
  cardContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  card: {
    width: '100%',
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 10,
    margin: 5,
    marginBottom: 20,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    elevation: 2,
  },
  cardImage: {
    width: '100%',
    height: 150,
    resizeMode: 'cover',
    borderRadius: 10,
  },
  hotelName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginTop: 10,
  },
  price: {
    fontSize: 16,
    marginTop: 5,
  },
  rating: {
    fontSize: 16,
    marginTop: 5,
  },
  views: {
    fontSize: 16,
    marginTop: 5,
  },
  Bedrooms: {
    fontSize: 16,
    marginTop: 5,
  },
  featuresContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginTop: 10,
  },
  feature: {
    fontSize: 14,
    marginRight: 10,
    backgroundColor: '#007acc',
    padding: 5,
    borderRadius: 5,
    color: 'white',
  },
  featureText: {
    color: 'white',
  },
  bookNowButton: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
  },
  bookNowText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
  },
  getStartedButton: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    marginTop: 20,
  },
  getStartedButtonText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
  },

  loginButtonText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
  },

  loginButton: {
    backgroundColor: 'red',
    padding: 10,
    borderRadius: 5,
    marginTop: 20,
  },

});
