import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert, StyleSheet, Picker } from 'react-native';
import axios from 'axios';
import DateTimePicker from '@react-native-community/datetimepicker';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function BookingScreen({ route }) {
  const { hotel } = route.params;
  const [checkInDate, setCheckInDate] = useState(new Date());
  const [checkOutDate, setCheckOutDate] = useState(new Date());
  const [numGuests, setNumGuests] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const [email, setEmail] = useState('');
  const [numMen, setNumMen] = useState(0);
  const [numWomen, setNumWomen] = useState(0);
  const [numChildren, setNumChildren] = useState(0);

  const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';

  const handleBooking = async () => {
    try {
      // Construct the booking data
      const bookingData = {
        hotelId: hotel.id,
        checkInDate,
        checkOutDate,
        numGuests,
        phoneNumber,
        address,
        email,
        numMen,
        numWomen,
        numChildren,
      };

      // Send a POST request to the server
      const response = await axios.post(`${serverUrl}booking.php`, bookingData);

      if (response.data.message === 'Booking Successful') {
        Alert.alert('Booking Successful', 'Your booking has been confirmed.');
      } else {
        Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      }
    } catch (error) {
      Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Make a Booking</Text>
      <Text style={styles.hotelName}>{hotel.name}</Text>
      <DateTimePicker
        value={checkInDate}
        mode="date"
        display="default"
        onChange={(event, selectedDate) => setCheckInDate(selectedDate)}
      />
      <DateTimePicker
        value={checkOutDate}
        mode="date"
        display="default"
        onChange={(event, selectedDate) => setCheckOutDate(selectedDate)}
      />
      <TextInput
        style={styles.input}
        placeholder="Number of Guests"
        onChangeText={(text) => setNumGuests(text)}
        value={numGuests}
        keyboardType="numeric"
      />
      <TextInput
        style={styles.input}
        placeholder="Phone Number"
        onChangeText={(text) => setPhoneNumber(text)}
        value={phoneNumber}
        keyboardType="phone-pad"
      />
      <TextInput
        style={styles.input}
        placeholder="Address"
        onChangeText={(text) => setAddress(text)}
        value={address}
      />
      <TextInput
        style={styles.input}
        placeholder="Email"
        onChangeText={(text) => setEmail(text)}
        value={email}
        keyboardType="email-address"
      />
      <View style={styles.pickerContainer}>
        <Picker
          style={styles.picker}
          selectedValue={numMen}
          onValueChange={(itemValue) => setNumMen(itemValue)}
        >
          <Picker.Item label="Men" value={0} />
          <Picker.Item label="1" value={1} />
          <Picker.Item label="2" value={2} />
          <Picker.Item label="3" value={3} />
        </Picker>
        <Picker
          style={styles.picker}
          selectedValue={numWomen}
          onValueChange={(itemValue) => setNumWomen(itemValue)}
        >
          <Picker.Item label="Women" value={0} />
          <Picker.Item label="1" value={1} />
          <Picker.Item label="2" value={2} />
          <Picker.Item label="3" value={3} />
        </Picker>
        <Picker
          style={styles.picker}
          selectedValue={numChildren}
          onValueChange={(itemValue) => setNumChildren(itemValue)}
        >
          <Picker.Item label="Children" value={0} />
          <Picker.Item label="1" value={1} />
          <Picker.Item label="2" value={2} />
          <Picker.Item label="3" value={3} />
        </Picker>
      </View>
      <Button title="Book Now" onPress={handleBooking} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  hotelName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    margin: 10,
    padding: 10,
  },
  pickerContainer: {
    flexDirection: 'row',
  },
  picker: {
    height: 50,
    width: 100,
    marginRight: 10,
  },
});
