import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert, StyleSheet } from 'react-native';
import axios from 'axios';

export default function BookingScreen({ route }) {
  const { hotel } = route.params;
  const [checkInDate, setCheckInDate] = useState('');
  const [checkOutDate, setCheckOutDate] = useState('');
  const [numGuests, setNumGuests] = useState('');

  const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';

  const handleBooking = async () => {
    try {
      // Construct the booking data
      const bookingData = {
        hotelId: hotel.id,
        checkInDate,
        checkOutDate,
        numGuests,
      };

      // Send a POST request to the server
      const response = await axios.post(`${serverUrl}booking.php`, bookingData);

      if (response.data.message === 'Booking Successful') {
        Alert.alert('Booking Successful', 'Your booking has been confirmed.');
      } else {
        Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      }
    } catch (error) {
      Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Make a Booking</Text>
      <Text style={styles.hotelName}>{hotel.name}</Text>
      <TextInput
        style={styles.input}
        placeholder="Check-In Date"
        onChangeText={(text) => setCheckInDate(text)}
        value={checkInDate}
      />
      <TextInput
        style={styles.input}
        placeholder="Check-Out Date"
        onChangeText={(text) => setCheckOutDate(text)}
        value={checkOutDate}
      />
      <TextInput
        style={styles.input}
        placeholder="Number of Guests"
        onChangeText={(text) => setNumGuests(text)}
        value={numGuests}
        keyboardType="numeric"
      />
      <Button title="Book Now" onPress={handleBooking} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  hotelName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    margin: 10,
    padding: 10,
  },
});
