export default function LoginScreen({ navigation }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
  
    const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';
  
    const loginUser = async () => {
      try {
        const response = await axios.post(`${serverUrl}login.php`, {
          username,
          password,
        });
  
        if (response.data.message === 'Login Successful') {
          // Navigate to the Dashboard screen if the login is successful
          navigation.navigate('Dashboard', { username });
  
          // Clear the input fields
          setUsername('');
          setPassword('');
        } else {
          // Display an error message for unsuccessful login
          Alert.alert('Login Error', 'Invalid username or password. Please try again.');
        }
      } catch (error) {
        // Display an error message for unsuccessful login
        Alert.alert('Login Error', 'Invalid username or password. Please try again.');
        console.error(error);
      }
    };
  
    return (
      <View style={styles.container}>
        <Text style={styles.header}>Login</Text>
        <TextInput
          style={styles.input}
          placeholder="Username"
          onChangeText={(text) => setUsername(text)}
          value={username}
          id="username"
        />
        <TextInput
          style={styles.input}
          placeholder="Password"
          secureTextEntry={true}
          onChangeText={(text) => setPassword(text)}
          value={password}
          id="password"
        />
        <Button title="Login" onPress={loginUser} />
      </View>
    );
  }
