import React from 'react';
import { View, Text, TextInput, StyleSheet, ScrollView, Image, TouchableOpacity } from 'react-native';

export default function WelcomeScreen({ navigation }) {
  const hotels = [
    {
      id: 1,
      name: 'Luxury Hotel',
      price: 150,
      starRating: 5,
      views: 20000,
      rooms: 100,
      features: ['Pool', 'Spa', 'Restaurant'],
      image: require('./hotel.jpg'),
    },
    {
      id: 2,
      name: 'Beach Resort',
      price: 120,
      starRating: 4,
      views: 25000,
      rooms: 80,
      features: ['Beach Access', 'Fitness Center'],
      image: require('./hotel2.jpg'),
    },
    {
      id: 3,
      name: 'City Retreat',
      price: 100,
      starRating: 4,
      views: 18000,
      rooms: 60,
      features: ['City View', 'Gym'],
      image: require('./hotel3.jpg'),
    },
  ];

  const handleGetStarted = () => {
    navigation.navigate('MainApp');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Discover the Best Hotels</Text>
      <TextInput style={styles.searchBar} placeholder="Search for hotels..." />
      <ScrollView contentContainerStyle={styles.cardContainer}>
        {hotels.map((hotel, index) => (
          <TouchableOpacity
            style={styles.card}
            key={hotel.id}
            onPress={() => navigation.navigate('HotelDetails', { hotel })}
          >
            <Image source={hotel.image} style={styles.cardImage} />
            <Text style={styles.hotelName}>{hotel.name}</Text>
            <Text style={styles.price}>${hotel.price} per night</Text>
            <Text style={styles.rating}>Rating: {hotel.starRating} stars</Text>
            <Text style={styles.views}>{hotel.views} views</Text>
            <Text style={styles.rooms}>Rooms: {hotel.rooms}</Text>
            <View style={styles.featuresContainer}>
              {hotel.features.map((feature, i) => (
                <Text key={i} style={styles.feature}>
                  {feature}
                </Text>
              ))}
            </View>
          </TouchableOpacity>
        ))}
      </ScrollView>
      <TouchableOpacity style={styles.getStartedButton} onPress={handleGetStarted}>
        <Text style={styles.getStartedButtonText}>Get Started</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    padding: 20,
    backgroundColor: 'lightgray',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  searchBar: {
    width: '100%',
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    padding: 10,
    marginBottom: 20,
  },
  cardContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  card: {
    width: '48%',
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 10,
    margin: 5,
    marginBottom: 20,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    elevation: 2,
  },
  cardImage: {
    width: '100%',
    height: 150,
    resizeMode: 'cover',
    borderRadius: 10,
  },
  hotelName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginTop: 10,
  },
  price: {
    fontSize: 16,
    marginTop: 5,
  },
  rating: {
    fontSize: 16,
    marginTop: 5,
  },
  views: {
    fontSize: 16,
    marginTop: 5,
  },
  rooms: {
    fontSize: 16,
    marginTop: 5,
  },
  featuresContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginTop: 10,
  },
  feature: {
    fontSize: 14,
    marginRight: 10,
    backgroundColor: '#007acc',
    padding: 5,
    borderRadius: 5,
    color: 'white',
  },
  getStartedButton: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
  },
  getStartedButtonText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
  },
});
