import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert, StyleSheet } from 'react-native';
import axios from 'axios';

export default function LoginScreen({ navigation }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';

  const loginUser = async () => {
    try {
      const response = await axios.post(`${serverUrl}login.php`, {
        username,
        password,
      });

      if (response.data.message === 'Login Successful') {
        // Navigate to the Dashboard screen if the login is successful
        navigation.navigate('Welcome', { username });

        // Clear the input fields
        setUsername('');
        setPassword('');
      } else {
        // Display an error message for unsuccessful login
        Alert.alert('Login Error', 'Invalid username or password. Please try again.');
      }
    } catch (error) {
      // Display an error message for unsuccessful login
      Alert.alert('Login Error', 'Invalid username or password. Please try again.');
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Login</Text>
      <TextInput
        style={styles.input}
        placeholder="Username"
        onChangeText={(text) => setUsername(text)}
        value={username}
        id="username"
      />
      <TextInput
        style={styles.input}
        placeholder="Password"
        secureTextEntry={true}
        onChangeText={(text) => setPassword(text)}
        value={password}
        id="password"
      />
      <Button title="Login" onPress={loginUser} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    margin: 10,
    padding: 10,
  },
});
