import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert, StyleSheet } from 'react-native';
import axios from 'axios';

export default function BookingScreen({ route }) {
    const { hotel } = route.params;
    const today = new Date(); // Get today's date
    const tomorrow = new Date();
    tomorrow.setDate(today.getDate() + 1); // Set check-out date to tomorrow by default
    
    const [formData, setFormData] = useState({
      hotel_id: hotel.id,
      check_in_date: formatDate(today), // Format the check-in date as YYYY-MM-DD
      check_out_date: formatDate(tomorrow), // Format the check-out date as YYYY-MM-DD
      num_guests: '1', // Default to '1' as a string
      phone_number: '',
      address: '',
      email: '',
      num_men: '0',
      num_women: '0',
      num_children: '0',
    });
    
    // ...
    
    function formatDate(date) {
      const year = date.getFullYear();
      const month = (date.getMonth() + 1).toString().padStart(2, '0'); // Month is 0-indexed
      const day = date.getDate().toString().padStart(2, '0');
      return `${year}-${month}-${day}`;
    }
    

  

  // ... (other state variables)

  const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';

  const handleBooking = async () => {
    try {
      // Send a POST request to the server
      const response = await axios.post(`${serverUrl}booking.php`, formData);

      if (response.data.message === 'Booking Successful') {
        Alert.alert('Booking Successful', 'Your booking has been confirmed.');
      } else {
        Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      }
    } catch (error) {
      Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Make a Booking</Text>
      <Text style={styles.hotelName}>{hotel.name}</Text>
      <View style={styles.input}>
        <Text>Check-In Date: {formData.checkInDate}</Text>
        <Button title="Select Check-In Date" onPress={showCheckInDatePicker} />
        <DateTimePicker
          isVisible={isCheckInDatePickerVisible}
          mode="date"
          onConfirm={handleCheckInDatePicked}
          onCancel={hideCheckInDatePicker}
        />
      </View>
      <View style={styles.input}>
        <Text>Check-Out Date: {formData.checkOutDate}</Text>
        <Button title="Select Check-Out Date" onPress={showCheckOutDatePicker} />
        <DateTimePicker
          isVisible={isCheckOutDatePickerVisible}
          mode="date"
          onConfirm={handleCheckOutDatePicked}
          onCancel={hideCheckOutDatePicker}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="phone" size={20} style={styles.icon} />
        <TextInput
          placeholder="Phone Number"
          onChangeText={(text) => setFormData({ ...formData, phoneNumber: text })}
          value={formData.phoneNumber}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="address-book" size={20} style={styles.icon} />
        <TextInput
          placeholder="Address"
          onChangeText={(text) => setFormData({ ...formData, address: text })}
          value={formData.address}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="envelope" size={20} style={styles.icon} />
        <TextInput
          placeholder="Email"
          onChangeText={(text) => setFormData({ ...formData, email: text })}
          value={formData.email}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="user" size={20} style={styles.icon} />
        <TextInput
          placeholder="Number of Men"
          onChangeText={(text) => setFormData({ ...formData, numMen: text })}
          value={formData.numMen}
          keyboardType="numeric"
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="user" size={20} style={styles.icon} />
        <TextInput
          placeholder="Number of Women"
          onChangeText={(text) => setFormData({ ...formData, numWomen: text })}
          value={formData.numWomen}
          keyboardType="numeric"
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="user" size={20} style={styles.icon} />
        <TextInput
          placeholder="Number of Children"
          onChangeText={(text) => setFormData({ ...formData, numChildren: text })}
          value={formData.numChildren}
          keyboardType="numeric"
        />
      </View>
      {/* ... (other input components) */}
      <Button title="Book Now" onPress={handleBooking} />
    </View>
  );
}



     
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  hotelName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  iconInput: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  icon: {
    marginRight: 10,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    margin: 10,
    padding: 10,
  },
  inputField: {
    flex: 1,
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    padding: 10,
  },
  datePicker: {
    width: '80%',
    margin: 10,
  },
});
