import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import axios from 'axios';


export default function WelcomeScreen({ route }) {
    const [username, setUsername] = useState('');
    const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';
  
    useEffect(() => {
      // Check if 'username' exists in 'route.params' before making the request
      if (route.params && route.params.username) {
        // Fetch the username of the currently logged-in user from the server
        axios
          .post(`${serverUrl}getUserDetails.php`, { username: route.params.username }) // Use 'username' instead of 'userId'
          .then((response) => {
            setUsername(response.data.username);
          })
          .catch((error) => {
            console.error(error);
          });
      } else {
        // Handle the case where 'username' is not available in 'route.params'
        console.error("Username not found in route.params");
      }
    }, [route.params]);
  
    return (
      <View style={styles.container}>
        <Text style={styles.header}>Dashboard</Text>
        <Text>Welcome, {username}!</Text>
      </View>
    );
  }
  

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
});
