import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert, StyleSheet } from 'react-native';
import axios from 'axios';
import Icon from 'react-native-vector-icons/FontAwesome';

export default function BookingScreen({ route }) {
  const { hotel } = route.params;
  const [isCheckInDatePickerVisible, setCheckInDatePickerVisible] = useState(false);
  const [isCheckOutDatePickerVisible, setCheckOutDatePickerVisible] = useState(false);
  const [checkInDate, setCheckInDate] = useState('');
  const [checkOutDate, setCheckOutDate] = useState('');
  const [numGuests, setNumGuests] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const [email, setEmail] = useState('');
  const [numMen, setNumMen] = useState(0);
  const [numWomen, setNumWomen] = useState(0);
  const [numChildren, setNumChildren] = useState(0);

  // ... (other state variables)

  const serverUrl = 'https://akinbrand.com.ng/mywi-fi/';

  const showCheckInDatePicker = () => {
    setCheckInDatePickerVisible(true);
  };

  const showCheckOutDatePicker = () => {
    setCheckOutDatePickerVisible(true);
  };

  const hideCheckInDatePicker = () => {
    setCheckInDatePickerVisible(false);
  };

  const hideCheckOutDatePicker = () => {
    setCheckOutDatePickerVisible(false);
  };

  const handleCheckInDatePicked = (date) => {
    setCheckInDate(date.toISOString()); // Format the date as needed
    hideCheckInDatePicker();
  };

  const handleCheckOutDatePicked = (date) => {
    setCheckOutDate(date.toISOString()); // Format the date as needed
    hideCheckOutDatePicker();
  };

  const handleBooking = async () => {
    try {
      // Construct the booking data
      const bookingData = {
        hotelId: hotel.id,
        checkInDate,
        checkOutDate,
        numGuests,
        phoneNumber,
        address,
        email,
        numMen,
        numWomen,
        numChildren,
        // ... (other data)
      };

      // Send a POST request to the server
      const response = await axios.post(`${serverUrl}booking.php`, bookingData);

      if (response.data.message === 'Booking Successful') {
        Alert.alert('Booking Successful', 'Your booking has been confirmed.');
      } else {
        Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      }
    } catch (error) {
      Alert.alert('Booking Error', 'An error occurred while booking. Please try again.');
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Make a Booking</Text>
      <Text style={styles.hotelName}>{hotel.name}</Text>
      <View style={styles.input}>
        <Text>Check-In Date: {checkInDate}</Text>
        <Button title="Select Check-In Date" onPress={showCheckInDatePicker} />
        <DateTimePicker
          isVisible={isCheckInDatePickerVisible}
          mode="date"
          onConfirm={handleCheckInDatePicked}
          onCancel={hideCheckInDatePicker}
        />
      </View>
      <View style={styles.input}>
        <Text>Check-Out Date: {checkOutDate}</Text>
        <Button title="Select Check-Out Date" onPress={showCheckOutDatePicker} />
        <DateTimePicker
          isVisible={isCheckOutDatePickerVisible}
          mode="date"
          onConfirm={handleCheckOutDatePicked}
          onCancel={hideCheckOutDatePicker}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="phone" size={20} style={styles.icon} />
        <TextInput
          placeholder="Phone Number"
          onChangeText={(text) => setPhoneNumber(text)}
          value={phoneNumber}
          style={styles.inputField}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="address-book" size={20} style={styles.icon} />
        <TextInput
          placeholder="Address"
          onChangeText={(text) => setAddress(text)}
          value={address}
          style={styles.inputField}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="envelope" size={20} style={styles.icon} />
        <TextInput
          placeholder="Email"
          onChangeText={(text) => setEmail(text)}
          value={email}
          style={styles.inputField}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="user" size={20} style={styles.icon} />
        <TextInput
          placeholder="Number of Men"
          onChangeText={(text) => setNumMen(text)}
          value={numMen}
          keyboardType="numeric"
          style={styles.inputField}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="user" size={20} style={styles.icon} />
        <TextInput
          placeholder="Number of Women"
          onChangeText={(text) => setNumWomen(text)}
          value={numWomen}
          keyboardType="numeric"
          style={styles.inputField}
        />
      </View>
      <View style={styles.iconInput}>
        <Icon name="user" size={20} style={styles.icon} />
        <TextInput
          placeholder="Number of Children"
          onChangeText={(text) => setNumChildren(text)}
          value={numChildren}
          keyboardType="numeric"
          style={styles.inputField}
        />
      </View>
      {/* ... (other input components) */}
      <Button title="Book Now" onPress={handleBooking} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  hotelName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  iconInput: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  icon: {
    marginRight: 10,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    margin: 10,
    padding: 10,
  },
  inputField: {
    flex: 1,
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    padding: 10,
  },
  datePicker: {
    width: '80%',
    margin: 10,
  },
});
